
public class ej1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int num=10;
		int val=num%2;
		int i=0;
		int a=10;
		if (val==0) {
			while(i<11) {
				int call=num*i;
				System.out.println(num + "X" + i + "=" + call );
				i++;
			}
			}
		else {
			while(a>=0) {
			int call=num*a;
			System.out.println(num + "X" + a + "=" + call );
			a--;
				
			}
				
				
		}

	}

}
